property.value:
/***sharedBlock***/

//Number of encoder pulses per revolution of the disc; this takes into account gearing ratio
#define ENCODER_TICKS_PER_REV	1000
#define DISC_DEFAULT_PERIOD		DISC_PERIOD_12RPS	//Default desired encoder pulse period (in us) for the disc; sets initial disc rate. 
#define RETURN_TO_DEFAULT_PERIOD					//Return to the default period after the ball has fallen through

#ifndef _UTILITY_H
#define _UTILITY_H

#define sign(x)					((x) < 0 ? -1 : 1)			//Signum function
#define ABS(x) 					((x) > 0 ? (x) : -(x))		//Absolute value
#define MIN(x, y)				((x) < (y) ? (x) : (y) )	//Least of two numbers
#define MAX(x, y)				((x) > (y) ? (x) : (y) )	//Greatest of two numbers
#define coerce(min, x, max) 	((x) < (min) ? (min) : ((x) > (max) ? (max) : (x)))	//Coerce a number to fall within a range

//Byte-level operations
#define LO(x)					((x) & 0xFF)			//Low order byte of a 16 bit number
#define HO(x)					((x) >> 0x08)			//High order byte of a 16 bit number
#define LLO(x)					((x) & 0xFF)			//Byte 0 (lowest order) byte of a 32-bit number
#define LHO(x)					(((x) >> 0x08) & 0xFF)	//Byte 1 of a 32-bit number
#define HLO(x)					(((x) >> 0x10) & 0xFF)	//Byte 2 of a 32-bit number
#define HHO(x)					((x) >> 0x18)			//Byte 3 (highest order) byte of a 32-bit number

typedef unsigned long long      uint64;
typedef unsigned long           uint32;
typedef unsigned int            uint16;
typedef unsigned char           uint8;
typedef uint8                   byte;
typedef signed long long        int64;
typedef signed long             int32;
typedef signed int              int16;
typedef signed char             int8;

#endif

#define DISC_SMALLEST_RATE		(~(1 << 30))	// Smallest rate (closest to zero) corresponds to the largest encoder period
						// Used as a reasonable initial value for Disc variables
//Dics position and period object captures the state of the disc
typedef struct{
	int32	position;		//Absolute position of the disc, in encoder ticks
	int32	period;			//Period between encoder ticks
} Disc;

extern const Disc Disc_0;
static volatile Disc g_desiredTrajectory = {0, DISC_SMALLEST_RATE};		//Planned state of the disc

/**/


/***preinitBlock***/
//Global variables
static Disc g_disc;
static volatile uint32 g_alignCount = 0;				// Number of alignment pulses received
static volatile uint8 g_alignEnabled = 0;				// Indicates if alignment is enabled
static volatile int32 	g_discPowerOffset = 0;		//Essentially a DC offset to overcome static friction; determined dynamically in main()
static volatile uint8	g_run = 1;					// Boolean, used to control start/stop state of the TBD
static volatile uint32	g_timeStart = 0;			// Time control loop is entered (following external input);				
static Time previousEncoderEventTime;
static int32	g_positionAtHole = 0;

//return the desired trajectory of the disc
void trajectory(Disc * const disc){
	*disc = g_desiredTrajectory;
}
//Change the period at which the desired position should be incremented - 
//	this is what sets the rate of the disc. Calling this function resets
//	the timer used to increment desired position; this may result in the
//	omission of a position increment since the timer may have already
//	counted down a fraction of the previous period, but it is better to
//	change the period now in case the difference between the old and new
//	periods is large (otherwise a significant delay would result).
void trajectoryPeriodSet(const int32 period){
	g_desiredTrajectory.period = period;
	TimerLoadSet(TIMER2_BASE, TIMER_A, convertNsecsToCycles(ABS(period) * 1000));
}

// Timed trajectory planner ISR. Change desired disc position,
// and if the planned rate has changed, reconfigure the timer.
void trajectoryHandler(void){
	TimerIntClear(TIMER2_BASE, TIMER_TIMA_TIMEOUT);

	// Change desired position
	g_desiredTrajectory.position += (g_desiredTrajectory.period > 0) ? 1 : -1;
}


// Find the change in period needed for the ball to fall through one hole
int32 trajectoryCorrectionPeriod(uint32 impactTime) {
	int32 positionProjectedChange;
	int32 positionCorrection;

	positionProjectedChange = impactTime / g_disc.period;
	positionCorrection = (g_disc.position + positionProjectedChange) % ENCODER_TICKS_PER_REV;

	if (positionCorrection < (ENCODER_TICKS_PER_REV / 4))	// If the disk needs to slow down for the ball to fall into the hole at 0 degrees
		return impactTime/(positionProjectedChange - positionCorrection);
	else if(positionCorrection >= (ENCODER_TICKS_PER_REV / 4) && positionCorrection < ((ENCODER_TICKS_PER_REV * 3)/4)) //For the hole at 180 degrees
		return impactTime/(positionProjectedChange + (ENCODER_TICKS_PER_REV / 2) - positionCorrection);
	else	// If the disk needs to speed up for the ball to fall into the hole at 0 degrees
		return impactTime/(positionProjectedChange + ENCODER_TICKS_PER_REV - positionCorrection);
}
/**/

/***initBlock***/
g_desiredTrajectory = Disc_0;
timeSet(ZERO_TIME, &previousEncoderEventTime);
g_disc = Disc_0;

SysCtlPeripheralEnable(SYSCTL_PERIPH_TIMER2);
TimerConfigure(TIMER2_BASE, TIMER_CFG_A_PERIODIC);

IntEnable(INT_TIMER2A);
TimerIntEnable(TIMER2_BASE, TIMER_TIMA_TIMEOUT);
//TimerDisable(TIMER2_BASE, TIMER_A);
/**/

/***fireBlock***/
{
Disc   discDesired = Disc_0;					//Desired disc position and rate
int32  discPositionError = 0;					//Difference between desired and actual position
static int32  discPositionPreviousError = 0;			//Disc position error from the previous control loop iteration
int32  errorDerivative = 0;						//Low pass filtered change of error

int32  proportional = 0;						//Proportional component of control signal
int32  integral = 0;							//Integral component of control signal
int32  derivative = 0;							//Derivative component of control signal
int32  offset = 0;								//Offset to account for friction
int32  controlSignal = 0;						//Control signal (sum of proportional, integral, and derivative components)
static int8 discStationary = 1;
static int32 encoderInputs;
static int8 discAligned = 0;

if ($hasToken(encoderTicks#0)) {
	discStationary = 0;
	encoderInputs = $get(encoderTicks#0);
	//Encoder pulse detected - record period (for rate)
	// and increment encoder count
	if(encoderInputs & ENCODER_PIN_A){
		static Time timeGap;
		const uint32 pinStatus = encoderInputs >> 8; //If encoder channel B is leading, then direction is negative
		g_disc.position += pinStatus ? -1 : 1;
		timeSub(currentModelTime, previousEncoderEventTime, &timeGap);
		//FIXME: get rid of the division.
		g_disc.period = timeGap.nsecs / 1000;
		timeSet(currentModelTime, &previousEncoderEventTime);
	}
	//Encoder alignment detected - occurs twice per rotation.
	//when aligning, set position to zero
	if (!discAligned && (encoderInputs & ENCODER_PIN_I)){
		g_disc.position = 0;
		discAligned = 1;
	} else if (encoderInputs & ENCODER_PIN_I){
		g_disc.position = g_disc.position & 0xfffffffc;
		g_positionAtHole  = g_disc.position;
	}  
}
if ($hasToken(dropSensor#0)) {
	trajectoryPeriodSet(trajectoryCorrectionPeriod($get(dropSensor#0)));
}

// The following is ordinary C code, except for
// the macro references to the input and output
// ports.
//If an external event has stopped the device, stop the motor and do nothing.
if(!g_run){
	$put(actuation, 0);
	return;
}

if ($hasToken(controlStart#0)) {
	if (discStationary) {
		g_discPowerOffset += MOTOR_MAX_SPEED >> 8;
		$put(actuation, g_discPowerOffset);
		return;
	}
	//ATOMIC: Capture state of the system
	IntMasterDisable();
	trajectory(&discDesired);			// Record planned trajectory
	IntMasterEnable();
	//END ATOMIC

	discPositionError = discDesired.position - g_disc.position;
	controlSignal = 0;
	//Kp
	proportional = discPositionError << 9;	//Kp = 2^shift
	controlSignal += proportional;

	//Ki
	//FIXME: Implement Ki with variable step size.
	// Only use integeral control when position error is low
	// so as not to accumulate too much in the integeral component
	if (discPositionError < 50){   			 
		   integral += discPositionError;
	//	   controlSignal += integral;
	}

	//Kd
	//FIXME: This Kd assumes fixed-step size;
	//		implement with variable step size.
	// 	errorDerivative(t) = 3/4*errorDerivative(t-1) + 1/4*e(t)
	//	e = error derivative (discPositionError - discPositionPreviousError)
	//  errorDerivative = exponential moving average of error derivative
	errorDerivative = (errorDerivative + (discPositionError - discPositionPreviousError))>>1;
	derivative = errorDerivative << 11;	// Kd / dt = 2^shift
	controlSignal += derivative;

	//DC offset
	controlSignal += g_discPowerOffset;

	//Send the control signal. motorSetPower handles saturation.
	$put(actuation, controlSignal);

	discPositionPreviousError = discPositionError;
}
}
