property.value:
/***preinitBlock***/

//Return the current disc position and encoder pulse period
void discState(Disc * const disc){
	*disc = g_disc;
}

const Disc Disc_0 = {0, DISC_SMALLEST_RATE};			// Disc with zero position and large encoder period (essentially zero rate)
static volatile Disc g_disc = {0, DISC_SMALLEST_RATE};	// Actual disc state

//Change the period at which the desired position should be incremented - 
//	this is what sets the rate of the disc. Calling this function resets
//	the timer used to increment desired position; this may result in the
//	omission of a position increment since the timer may have already
//	counted down a fraction of the previous period, but it is better to
//	change the period now in case the difference between the old and new
//	periods is large (otherwise a significant delay would result).
void trajectoryPeriodSet(const int32 period){
	g_desiredTrajectory.period = period;
	TimerLoadSet(TIMER0_BASE, TIMER_A, microsecondsToCycles(ABS(period)));
}

//Initialize (but do not enable) timers for trajectory planning
void trajectoryInit(void){
	g_desiredTrajectory = Disc_0;

	SysCtlPeripheralEnable(SYSCTL_PERIPH_TIMER0);
	TimerConfigure(TIMER0_BASE, TIMER_CFG_A_PERIODIC);

	IntEnable(INT_TIMER0A);
	TimerIntEnable(TIMER0_BASE, TIMER_TIMA_TIMEOUT);
	TimerDisable(TIMER0_BASE, TIMER_A);
}

////////////////////////
// Control parameters //
////////////////////////

#define DISC_DEFAULT_PERIOD	125						//Default desired encoder pulse period (in us) for the disc; sets initial disc rate.
													//	1 encoder pulse / 125 us = 1000 pulses / 125 ms = 1 rot / 125 ms = 8 rps
#define CONTROL_FIXED_STEP							//If defined, force a minimum step size; if the WCET of the control loop is less than
													//	the minimum step size, then the controller will have a fixed step size.												//	loop is always an integer multiple of the minimum step size.
#define CONTROL_STEP_PERIOD	12						//Exponent for minimum period for each control cycle; 2^period step size;
													//	2^12 us <=> 244 Hz	(no jitter with UART debugging @ 115 Kbaud)
//Issue warnings if debugging features will cause execution times greater than the minimum step size
#ifdef CONTROL_FIXED_STEP
	//32-byte UART debug messages @ 115Kbaud can be transmitted in less than the minimum step size
	//	for step periods greater than or equal to than 2^12 us. Periods smaller than 2^12us (~244Hz)
	//	will experience jitter (or will be in fact bounded by the baud rate).
	#if (CONTROL_STEP_PERIOD < 12) && defined(UART_DEBUG_ENABLED)
		#warning "UART debugging will impact step size."
	#endif
	//Updating the display can cause a 3-4ms delay; periods smaller than 2^15us (~30Hz)
	//	will experience jitter.
	#if (CONTROL_STEP_PERIOD < 15) && defined(DISPLAY_ENABLED)
		#warning "Onscreen display will impact step size."
	#endif
#endif

static volatile int32 	g_discPowerOffset = 0;		//Essentially a DC offset to overcome static friction; determined dynamically in main()
static volatile uint8	g_run = 0;					// Boolean, used to control start/stop state of the TBD
static volatile uint32	g_timeStart = 0;			// Time control loop is entered (following external input);
													// 	used so measurements can begin at time t=0 by subtracting offset from system time.
/**/

/***initBlock***/
{
#ifdef DISPLAY_DEBUG_ENABLED
	uint32 displayMask = 0;						//Screen update timing mask for frame rate limiting
#endif

Disc   disc = Disc_0;							//Actual disc position and rate
Disc   discDesired = Disc_0;					//Desired disc position and rate
int32  discPositionError = 0;					//Difference between desired and actual position
int32  discPositionPreviousError = 0;			//Disc position error from the previous control loop iteration
int32  previousAlignCount = 0;					//Counts encoder alignment pulses; used to align disc

int32  proportional = 0;						//Proportional component of control signal
int32  integral = 0;							//Integral component of control signal
int32  derivative = 0;							//Derivative component of control signal
int32  controlSignal = 0;						//Control signal (sum of proportional, integral, and derivative components)

//50 MHz clock
SysCtlClockSet(SYSCTL_SYSDIV_4 | SYSCTL_USE_PLL | SYSCTL_OSC_MAIN | SYSCTL_XTAL_8MHZ);

//Set global variables
g_run = 0;
timerInit();
gpioInit();
pwmInit();
encoderInit();
dropSensorInit();
trajectoryInit();
}
/**/

/***fireBlock***/
if ($hasToken(dropSensor, 0)) {
	trajectoryPeriodSet($get(dropSensor#0));
}

// The following is ordinary C code, except for
// the macro references to the input and output
// ports.
//If an external event has stopped the device, stop the motor and do nothing.
if(!g_run){
	motorSetPower(0);
	continue;
}

//ATOMIC: Capture state of the system
IntMasterDisable();
discState(&disc);				// Record disc position and encoder pulse period
trajectory(&discDesired);			// Record planned trajectory
IntMasterEnable();
//END ATOMIC

discPositionError = discDesired.position - disc.position;
controlSignal = 0;
//Kp
proportional = discPositionError << 7;	//Kp = 2^shift
controlSignal += proportional;

//Ki

//Kd
//FIXME: Kd assumes a fixed steps size - instead must calculate
//	based on actual step, dt.
derivative = (discPositionError - discPositionPreviousError) << 10;	// Kd / dt = 2^shift
controlSignal += derivative;

//DC offset
controlSignal += g_discPowerOffset;

//Send the control signal. motorSetPower handles saturation.
$put(actuation, controlSignal);
/**/

/***wrapupBlock***/
/**/
